INSERT INTO `email_configs` (`id`, `module`, `action`, `subject`, `html_template`, `smtp_host`, `smtp_secure`, `smtp_port`, `smtp_username`, `smtp_password`, `from_email`, `from_name`, `status`, `variables`, `to`, `cc`, `bcc`, `createdAt`, `updatedAt`) VALUES
(1, 'admin', 'forgot-password', 'Forgot Password', '<div style=\"font-family: Arial, sans-serif; color: #333;\">\n  <h2>Password Reset Request</h2>\n  <p>Dear {{name}},</p>\n  <p>\n    You requested to reset your password for your account \n    (<strong>{{email}}</strong>).\n  </p>\n  <p>Please click the link below to reset your password:</p>\n  <p>\n    <a href=\"{{resetUrl}}\" style=\"color: #007BFF; font-weight: bold;\">\n      Reset Password\n    </a>\n  </p>\n  <p>\n    This link is valid until <strong>{{expiry}}</strong>.\n  </p>\n  <p>\n    If you did not request this, you can safely ignore this email.\n  </p>\n  <br/>\n  <p>Regards,<br/><strong>{{appName}} Team</strong></p>\n  <p style=\"font-size: 12px; color: #888;\">\n    &copy; {{year}} {{appName}}. All rights reserved.\n  </p>\n</div>\n', 'smtp.gmail.com', 1, 465, 'kapilakshu848@gmail.com', 'zlgninioettnhazf', 'kapilakshu848@gmail.com', 'Synco', 1, NULL, '\"[{\\\"name\\\":\\\"{{name}}\\\",\\\"email\\\":\\\"{{email}}\\\"}]\"', NULL, NULL, '2025-07-04 09:23:31', '2025-07-04 09:23:31'),
(2, 'admin', 'create admin', 'You’ve been invited to the Admin Panel', '<div style=\"background-color: #f6f7fb; padding: 40px; font-family: Arial, sans-serif;\">\n  <div style=\"max-width: 600px; margin: auto; background: #ffffff; border-radius: 8px; overflow: hidden; box-shadow: 0 2px 8px rgba(0,0,0,0.05);\">\n\n    <!-- Logo -->\n    <div style=\"text-align: left; padding: 30px;\">\n      <img src=\"{{logoUrl}}\" alt=\"{{appName}} Logo\" style=\"max-width: 100px; height: auto;\">\n    </div>\n\n    <!-- Email content -->\n    <div style=\"padding: 30px;padding-top: 0px;\">\n      <h2 style=\"font-size: 22px; margin-top: 0; margin-bottom: 20px;\">\n        Welcome to {{appName}}\n      </h2>\n      \n      <p style=\"font-size: 16px; color: #333; margin-bottom: 15px;\">\n        Hello <strong>{{firstName}} {{lastName}}</strong>,\n      </p>\n      \n      <p style=\"font-size: 15px; color: #555; margin-bottom: 15px; line-height: 1.6;\">\n        Your administrator account for <strong>{{appName}}</strong> has been successfully created.  \n        To activate your account, please set your password using the secure link below:\n      </p>\n      \n      <p style=\"font-size: 15px; margin: 20px 0;\">\n        <a href=\"{{resetLink}}\" target=\"_blank\" style=\"color: #007bff; word-break: break-all; text-decoration: none;\">\n          {{resetLink}}\n        </a>\n      </p>\n      \n      <p style=\"font-size: 14px; color: #888; margin-top: 20px;\">\n        ⚠️ For security reasons, this link will expire in <strong>24 hours</strong>.\n      </p>\n      \n      <hr style=\"border: none; border-top: 1px solid #e0e0e0; margin: 30px 0;\">\n      \n      <p style=\"font-size: 14px; color: #555; line-height: 1.6;\">\n        Best regards,<br>\n        <strong>{{appName}} Team</strong>\n      </p>\n      \n      <p style=\"font-size: 12px; color: #999; margin-top: 20px;\">\n        &copy; {{year}} {{appName}}. All rights reserved.\n      </p>\n    </div>\n  </div>\n</div>\n', 'smtp.gmail.com', 1, 465, 'kapilakshu848@gmail.com', 'zlgninioettnhazf', 'kapilakshu848@gmail.com', 'Synco', 1, '[\"name\",\"loginUrl\"]', NULL, NULL, NULL, '2025-07-17 10:00:00', '2025-07-17 10:00:00'),
(3, 'admin', 'free-trial-confirmation', 'Your Free Trial Booking is Confirmed!', '<div style=\"max-width:600px; background-color:#fff; margin:0 auto; font-family: \'Google Sans\', Arial, sans-serif;\">\n\n    <!-- Banner Section -->\n    <section style=\"text-align:center; padding: 20px 0;\">\n        <img src=\"{{logoUrl}}\" alt=\"Company Logo\" style=\"width:150px; max-width:80%; height:auto;\" />\n    </section>\n\n    <!-- Intro Section -->\n    <section style=\"padding:20px 0 0 0;\">\n        <div style=\"max-width:465px; padding:0 15px; margin:0 auto;\">\n\n            <div style=\"text-align:center; padding-bottom:0; margin:0 auto;\">\n                <p style=\"font-size:14px; line-height:23px; color:#5F5F6D; font-weight:400; margin:0; padding-top:12px;\">\n                    We can\'t wait to welcome your child to our classes.<br>\n                    We\'ve tried to include as much information below as possible to make your Samba Soccer experience run smoothly.\n                </p>\n            </div>\n\n            <div style=\"text-align:center; margin:20px 0; padding:10px; background:#f5f5f5; border-radius:8px;\">\n                <p style=\"font-size:14px; color:#34353B; margin:5px 0;\">\n                    Your login credentials for the parent portal:\n                </p>\n                <p style=\"font-size:14px; color:#5F5F6D; margin:5px 0;\">\n                    Email: <strong>{{parentEmail}}</strong><br>\n                    Password: <strong>{{parentPassword}}</strong>\n                </p>\n            </div>\n\n            <!-- Make a Note Heading -->\n            <div>\n                <h2 style=\"text-align:center; margin-top:0; font-family:\'Permanent Marker\'; color:#00a6e3; font-size:40px; padding:24px 0 0; text-transform:capitalize;\">\n                    <img src=\"/find-img/Make-a-Note.png\" alt=\"\" />\n                </h2>\n            </div>\n\n            <!-- Student Info -->\n            <div style=\"border-top:4px solid #0DD180; border-radius:10px; padding:10px; margin:0 0 20px; background-color:#f5f5f5;\">\n                <table style=\"width:100%; border-collapse:collapse;\">\n                    <tbody>\n                        <tr>\n                            <td style=\"width:30%; padding:5px; vertical-align:top;\">\n                                <p style=\"margin:0; font-size:13px; color:#34353B; font-weight:600;\">Name of Student(s):</p>\n                                <p style=\"margin:0; font-size:13px; color:#5F5F6D;\">{{studentFirstName}} {{studentLastName}}</p>\n                            </td>\n                            <td style=\"width:20%; padding:5px; vertical-align:top;\">\n                                <p style=\"margin:0; font-size:13px; color:#34353B; font-weight:600;\">Date:</p>\n                                <p style=\"margin:0; font-size:13px; color:#5F5F6D;\">{{trialDate}}</p>\n                            </td>\n                            <td style=\"width:20%; padding:5px; vertical-align:top;\">\n                                <p style=\"margin:0; font-size:13px; color:#34353B; font-weight:600;\">Age Group:</p>\n                                <p style=\"margin:0; font-size:13px; color:#5F5F6D;\">{{className}}</p>\n                            </td>\n                            <td style=\"width:35%; padding:5px; vertical-align:top;\">\n                                <p style=\"margin:0; font-size:13px; color:#34353B; font-weight:600;\">Time:</p>\n                                <p style=\"margin:0; font-size:13px; color:#5F5F6D;\">{{classTime}}</p>\n                            </td>\n                        </tr>\n                    </tbody>\n                </table>\n            </div>\n\n            <!-- Venue Section -->\n            <div style=\"border-top:4px solid #0DD180; border-radius:10px; padding:20px; margin:20px 0; background-color:#5f5f6d17;\">\n                <h3 style=\"margin:0; font-weight:600; font-size:15px; color:#34353B; padding-bottom:5px;\">Venue</h3>\n                <p style=\"margin:0; font-size:13px; color:#5F5F6D;\">\n                    <a href=\"#\" style=\"text-decoration:underline; color:#34353B;\">{{venueName}}</a>\n                    <a href=\"https://www.google.com/maps/search/?api=1&query={{venueName}}\" style=\"font-size:13px; text-decoration:underline; color:#5F5F6D; padding-left:5px;\">See link here</a>\n                </p>\n            </div>\n\n            <!-- Image Section -->\n            <div style=\"text-align:center; margin:50px 0 14px;\">\n                <img src=\"{{kidsPlaying}}\" alt=\"Kids playing soccer\" style=\"width:100%;\" />\n            </div>\n\n\n            <!-- Key Info Button -->\n            <div style=\"text-align:center; margin-bottom:22px; margin-top:40px;\">\n                <button style=\"font-family: \'Google Sans\', Arial, sans-serif; background-color:#FFDE14; padding:8px 16px; border:none; border-radius:14px; color:#042C89; font-size:18px; font-weight:900;\">Key Information</button>\n            </div>\n\n            <!-- Info Sections -->\n            <div>\n                <b style=\"color:#042C89; font-weight:600; font-size:17px;\">Arrival Time</b>\n                <p style=\"color:#3E3E47; font-size:13px; padding:5px 0 30px 0; margin:0;\">Please arrive at least 10 minutes before the session begins.</p>\n\n                <b style=\"color:#042C89; font-weight:600; font-size:17px;\">What to bring</b>\n                <p style=\"color:#3E3E47; font-size:13px; padding:5px 0 30px 0; margin:0;\">Your child should wear appropriate clothing for their free trial lesson. Normal trainers or astroturf trainers are acceptable for both indoor and outdoor venues, but studded or rubber boots are not allowed. We recommend bringing plenty of water for your child to drink during the session (no fizzy drinks, please).</p>\n\n                <b style=\"color:#042C89; font-weight:600; font-size:17px;\">Free Uniform Offer</b>\n                <p style=\"color:#3E3E47; font-size:13px; padding:5px 0 30px 0; margin:0;\">\n                    If you\'re happy with what we offer after the free trial lesson, sign up to become a member within 24 hours of the trial lesson and receive a full free uniform (t-shirt, shorts, socks).<br>\n                    Book membership: <a href=\"https://webstepdev.com/demo/synco/\" style=\"color:#3E3E47;\">https://webstepdev.com/demo/synco/</a><br>\n                    Uniform details: <a href=\"https://shop.sambasoccerschools.com/collections/essentials\" style=\"color:#3E3E47;\">https://webstepdev.com/demo/synco/</a>\n                </p>\n\n                <b style=\"color:#042C89; font-weight:600; font-size:17px;\">Cancellations</b>\n                <p style=\"color:#3E3E47; font-size:13px; padding:5px 0 30px 0; margin:0;\">Samba Soccer Schools reserves the right to cancel or reschedule any booking, including free trial lesson bookings, due to overbooking or unforeseen circumstances. Parents will be notified via text and added to our waiting list if applicable.</p>\n                <p style=\"color:#3E3E47; font-size:13px; padding:5px 0 30px 0; margin:0;\">We will make every effort to provide alternative booking options, but cannot guarantee availability. The safety of children is our priority.</p>\n\n                <b style=\"color:#042C89; font-weight:600; font-size:17px;\">Social Media</b>\n                <p style=\"color:#3E3E47; font-size:13px; padding:5px 0 30px 0; margin:0;\">\n                    Stay up-to-date with our latest offers, stories, and weekly photographs by subscribing to our Facebook and Instagram pages.<br>\n                    Visit our website: <a href=\"https://webstepdev.com/demo/synco/\" style=\"color:#3E3E47;\">https://webstepdev.com/demo/synco/</a>\n                </p>\n            </div>\n\n            <p style=\"color:#00a6e3; font-size:17px; text-align:center; margin:40px 0; font-weight:500;\">\n                We look forward to meeting you on our course. If you have any questions, please call us on 020 7205 2723.\n            </p>\n\n            <!-- Banner Image -->\n            <section style=\"text-align:center; padding: 20px 0;\">\n                <img src=\"{{logoUrl}}\" alt=\"Company Logo\" style=\"width:150px; max-width:80%; height:auto;\" />\n            </section>\n\n            <p style=\"color:#797A88; font-size:13px; padding:4px 42px; text-align:center; line-height:23px;\">\n                Looking to help your child prepare for their free trial lesson?<br>\n                Why not practice the ball mastery moves below with them at home or in a nearby park?\n            </p>\n\n            <!-- Skills Section -->\n            <div style=\"display:flex; flex-wrap:wrap; gap:10px; margin-top:30px;\">\n                <!-- Skill 1 -->\n                <div style=\"padding:10px; border:1px solid #eee; border-radius:10px; flex:1 1 48%; box-sizing:border-box; background-color:#fff;\">\n                    <a href=\"https://www.youtube.com/watch?v=JEWKJbo6jSg&feature=youtu.be\" style=\"text-decoration:none;\">\n                        <img src=\"/img-form/rounded3.png\" alt=\"The Rolar\" style=\"width:100%; border-radius:10px;\" />\n                        <div style=\"padding:5px;\">\n                            <p style=\"font-size:15px; font-weight:700; color:#042c89; margin:10px 0;\">The Rolar</p>\n                            <p style=\"font-size:12px; color:#5f5f6d; font-weight:400;\">Click the image above to learn the \"Rolar\". This is a simple ball mastery skill that is effective for moving past defenders.</p>\n                        </div>\n                    </a>\n                </div>\n\n                <!-- Skill 2 -->\n                <div style=\"padding:10px; border:1px solid #eee; border-radius:10px; flex:1 1 48%; box-sizing:border-box; background-color:#fff;\">\n                    <a href=\"https://www.youtube.com/watch?v=C49Bos5QAFw\" style=\"text-decoration:none;\">\n                        <img src=\"/img-form/rounded2.png\" alt=\"The Fatia\" style=\"width:100%; border-radius:10px;\" />\n                        <div style=\"padding:5px;\">\n                            <p style=\"font-size:15px; font-weight:700; color:#042c89; margin:10px 0;\">The Fatia</p>\n                            <p style=\"font-size:12px; color:#5f5f6d; font-weight:400;\">Click the image above to learn the \"Fatia\" skill! This skill is perfect for improving ball control and close touch dribbling.</p>\n                        </div>\n                    </a>\n                </div>\n\n                <!-- Skill 3 -->\n                <div style=\"padding:10px; border:1px solid #eee; border-radius:10px; flex:1 1 48%; box-sizing:border-box; background-color:#fff;\">\n                    <a href=\"https://www.youtube.com/watch?v=g24r83kQr_0&ab_channel=SambaSoccerSchools%C2%AE\" style=\"text-decoration:none;\">\n                        <img src=\"/img-form/rounded1.png\" alt=\"The Inside Pedalada Turn\" style=\"width:100%; border-radius:10px;\" />\n                        <div style=\"padding:5px;\">\n                            <p style=\"font-size:15px; font-weight:700; color:#042c89; margin:10px 0;\">The Inside Pedalada Turn</p>\n                            <p style=\"font-size:12px; color:#5f5f6d; font-weight:400;\">Click the image above to learn the \"Rolar\". This is a simple ball mastery skill that is effective for moving past defenders.</p>\n                        </div>\n                    </a>\n                </div>\n\n                <!-- Skill 4 -->\n                <div style=\"padding:10px; border:1px solid #eee; border-radius:10px; flex:1 1 48%; box-sizing:border-box; background-color:#fff;\">\n                    <a href=\"https://www.youtube.com/watch?v=gP5aUR5BGpU&ab_channel=SambaSoccerSchools%C2%AE\" style=\"text-decoration:none;\">\n                        <img src=\"/img-form/rounded.png\" alt=\"The Roll Back\" style=\"width:100%; border-radius:10px;\" />\n                        <div style=\"padding:5px;\">\n                            <p style=\"font-size:15px; font-weight:700; color:#042c89; margin:10px 0;\">The Roll Back</p>\n                            <p style=\"font-size:12px; color:#5f5f6d; font-weight:400;\">Click the image above to learn the \"Rolar\". This is a simple ball mastery skill that is effective for moving past defenders.</p>\n                        </div>\n                    </a>\n                </div>\n\n            </div>\n\n        </div>\n    </section>\n\n</div>', 'smtp.gmail.com', 1, 465, 'kapilakshu848@gmail.com', 'zlgninioettnhazf', 'kapilakshu848@gmail.com', 'Synco', 1, '[ \"{{parentName}}\", \"{{venueName}}\", \"{{className}}\", \"{{classTime}}\", \"{{trialDate}}\", \"{{studentName}}\" ]', '\"[{\\\"name\\\":\\\"{{parentName}}\\\",\\\"email\\\":\\\"{{parentEmail}}\\\"}]\"', NULL, NULL, '2025-07-21 11:43:04', '2025-07-21 11:43:04'),
(4, 'admin', 'free-trial update confirmation', 'Your Free Trial Booking Has Been Updated', '<div style=\"font-family: Arial, sans-serif; background-color: #f8f9fa; padding: 30px; color: #333;\">\n  <table style=\"max-width: 600px; margin: 0 auto; background: #fff; border-radius: 8px; overflow: hidden; box-shadow: 0 4px 10px rgba(0,0,0,0.05);\">\n    <tr>\n      <td style=\"background: #f39c12; padding: 20px; text-align: center; color: #fff;\">\n        <h1 style=\"margin: 0; font-size: 22px; letter-spacing: 0.5px;\">🔄 Free Trial Booking Updated</h1>\n      </td>\n    </tr>\n    <tr>\n      <td style=\"padding: 25px;\">\n        <h2 style=\"color:#2c3e50; font-size: 20px; margin-bottom: 10px;\">Hi {{parentName}},</h2>\n        <p style=\"font-size: 15px; line-height: 1.6;\">\n          We wanted to let you know that your <strong>free trial booking has been updated</strong>. Here are your latest booking details:\n        </p>\n        <div style=\"background: #f1f4f8; padding: 15px; border-left: 4px solid #e67e22; margin: 20px 0;\">\n          <p style=\"margin: 0; font-size: 15px; line-height: 1.6;\">\n            <strong>📍 Venue:</strong> {{venueName}} <br>\n            <strong>📅 New Date:</strong> {{trialDate}} <br>\n            <strong>⏰ Time:</strong> {{classTime}} <br>\n            <strong>🎓 Class:</strong> {{className}} <br>\n            <strong>👩‍🎓 Student:</strong> {{studentFirstName}} {{studentLastName}}\n          </p>\n        </div>\n        <p style=\"font-size: 15px; line-height: 1.6;\">\n          ✅ Please arrive <strong>10 minutes early</strong>.<br>\n          ✅ Bring a water bottle & comfortable sports shoes.<br>\n          ✅ Our team will guide you upon arrival.\n        </p>\n        <p style=\"font-size: 15px; line-height: 1.6; margin-top: 20px;\">\n          If you have any questions or need to reschedule, feel free to reach out.\n        </p>\n        <p style=\"margin-top: 25px; font-size: 15px;\">\n          Warm regards, <br>\n          <strong>{{appName}} Team</strong>\n        </p>\n      </td>\n    </tr>\n    <tr>\n      <td style=\"background: #f1f4f8; padding: 15px; text-align: center; font-size: 13px; color: #666;\">\n        &copy; {{year}} {{appName}}. All rights reserved.\n      </td>\n    </tr>\n  </table>\n</div>', 'smtp.gmail.com', 1, 465, 'kapilakshu848@gmail.com', 'zlgninioettnhazf', 'kapilakshu848@gmail.com', 'Synco', 1, '[ \"{{parentName}}\", \"{{venueName}}\", \"{{className}}\", \"{{trialDate}}\", \"{{classTime}}\", \"{{firstName}}\", \"{{lastName}}\", \"{{appName}}\", \"{{year}}\" ]', NULL, NULL, NULL, '2025-07-23 11:06:10', '2025-07-23 11:06:10'),
(6, 'admin', 'free-trial-rebooking', 'Your Free Trial Has Been Rebooked', '<div style=\"font-family: Arial, sans-serif; padding:20px; background-color:#f6f7fb; color:#333; line-height:1.6;\">\n  <h2 style=\"color:#4CAF50; margin-bottom:10px;\">Hello {{parentName}},</h2>\n\n  <p>Your child <strong>{{studentFirstName}} {{studentLastName}}</strong> has been successfully <b>rebooked</b> for a free trial.</p>\n\n  <p>Here are the updated details:</p>\n\n  <ul style=\"background:#fff; padding:15px; border-radius:8px; list-style:none; box-shadow:0 2px 5px rgba(0,0,0,0.1);\">\n    <li>📍 <b>Venue:</b> {{venueName}}</li>\n    <li>🎓 <b>Class:</b> {{className}}</li>\n    <li>📅 <b>New Date:</b> {{trialDate}}</li>\n    <li>⏰ <b>Time:</b> {{classTime}}</li>\n  </ul>\n\n  <p style=\"margin-top:15px;\">✅ Please arrive 10 minutes early.<br>\n  ✅ Bring a water bottle & comfortable sports shoes.<br>\n  ✅ Our team will guide you upon arrival.</p>\n\n  {{additionalNoteSection}}\n\n  <p>If you have any questions or need to reschedule, feel free to reply to this email.</p>\n\n  <p style=\"margin-top:20px;\">\n    Warm regards,<br>\n    <strong>{{appName}} Team</strong><br>\n    © {{year}} {{appName}}. All rights reserved.\n  </p>\n</div>\n', 'smtp.gmail.com', 1, 465, 'kapilakshu848@gmail.com', 'zlgninioettnhazf', 'kapilakshu848@gmail.com', 'Synco', 1, '[ \"{{parentName}}\", \"{{studentName}}\", \"{{venueName}}\", \"{{className}}\", \"{{trialDate}}\", \"{{additionalNote}}\" ]', NULL, NULL, NULL, '2025-07-25 17:23:05', '2025-07-25 17:23:05'),
(7, 'cancel-class', 'cancel', 'Class Cancellation Notification', '<div style=\"font-family: Arial, sans-serif; color: #333;\">\n  <h2>Class Cancelled</h2>\n  <p>Hello {{firstName}},</p>\n\n  <p>\n    We regret to inform you that the class \n    <strong>{{className}}</strong> scheduled at \n    <strong>{{venueName}}</strong> has been cancelled.\n  </p>\n\n  <p><strong>Reason:</strong> {{cancelReason}}</p>\n\n  <p>If you have any questions, please contact us.</p>\n\n  <br>\n  <p>Thanks,<br>The Synco Team</p>\n</div>\n', 'smtp.gmail.com', 1, 465, 'kapilakshu848@gmail.com', 'zlgninioettnhazf', 'kapilakshu848@gmail.com', 'Synco', 1, '[\"{{name}}\", \"{{className}}\", \"{{venueName}}\", \"{{cancelReason}}\"]', '[\"{{name}}\", \"{{className}}\", \"{{venueName}}\", \"{{cancelReason}}\", \"{{cancelDate}}\"]\n', NULL, NULL, '2025-07-28 17:06:35', '2025-07-28 17:06:35'),
(8, 'admin', 'cancel-trial', 'Your  Booking Has Been Cancelled', '<div style=\"font-family: Arial, sans-serif; background-color: #f6f7fb; padding: 40px;\">\n        <div style=\"max-width: 600px; margin: 0 auto; background-color: #ffffff; padding: 20px; border-radius: 8px;\">\n            <h2 style=\"color: #333333;\">Hello {{parentName}},</h2>\n            <p>We are sorry to inform you that your booking for {{studentName}} at {{venueName}} has been cancelled.</p>\n            <p><strong>Class:</strong> {{className}}</p>\n            <p><strong>Date:</strong> {{trialDate}}</p>\n               <p><strong>Time:</strong> {{startTime}} - {{endTime}}</p>\n            {{additionalNoteSection}}\n            <p>Thank you for your understanding.</p>\n            <p>– {{appName}} Team</p>\n        </div>\n    </div>', 'smtp.gmail.com', 1, 465, 'kapilakshu848@gmail.com', 'zlgninioettnhazf', 'kapilakshu848@gmail.com', 'Synco', 1, '[ \"{{parentName}}\", \"{{studentName}}\", \"{{venueName}}\", \"{{className}}\", \"{{trialDate}}\", \"{{classTime}}\", \"{{additionalNoteSection}}\", \"{{appName}}\" ]', NULL, NULL, NULL, '2025-08-12 13:46:54', '2025-08-12 13:46:54'),
(9, 'admin', 'no-membership-trial', 'No Membership After Trial', '<div style=\"font-family: Arial, sans-serif; background-color: #f6f7fb; padding: 40px;\">\n        <div style=\"max-width: 600px; margin: 0 auto; background-color: #ffffff; padding: 20px; border-radius: 8px;\">\n            <h2 style=\"color: #333333;\">Hello {{parentName}},</h2>\n            <p>Thank you for attending the free trial class for {{studentName}} at {{venueName}}.</p>\n            <p><strong>Class:</strong> {{className}}</p>\n            <p><strong>Date:</strong> {{trialDate}}</p>\n                <p><strong>Time:</strong> {{startTime}} - {{endTime}}</p>\n            <p><strong>Reason for not proceeding:</strong> {{reason}}</p>\n            {{notesSection}}\n            <p>We value your feedback and hope to see you again in the future.</p>\n            <p>Regards,<br>The {{appName}} Team</p>\n        </div>\n    </div>', 'smtp.gmail.com', 1, 465, 'kapilakshu848@gmail.com', 'zlgninioettnhazf', 'kapilakshu848@gmail.com', 'Synco', 1, '[ \"{{parentName}}\", \"{{studentName}}\", \"{{venueName}}\", \"{{className}}\", \"{{trialDate}}\", \"{{classTime}}\", \"{{reason}}\", \"{{notesSection}}\", \"{{appName}}\" ]', NULL, NULL, NULL, '2025-08-12 16:10:43', '2025-08-12 16:10:43'),
(10, 'admin', 'booking-status', 'Booking Status Update', '<div style=\"font-family: Arial, sans-serif; color:#333;\">\n       <p>Hello {{parentName}},</p>\n       <p>This is to inform you that the booking for your child <strong>{{studentName}}</strong> has the following status:</p>\n       <p><strong>Status: {{status}}</strong></p>\n       <p>Thank you,<br>Synco Team</p>\n     </div>', 'smtp.gmail.com', 1, 465, 'kapilakshu848@gmail.com', 'zlgninioettnhazf', 'kapilakshu848@gmail.com', 'Synco', 1, '[ \"{{parentName}}\", \"{{studentName}}\", \"{{status}}\" ]', NULL, NULL, NULL, '2025-08-18 17:04:41', '2025-08-18 17:04:41'),
(11, 'admin', 'book-paid-trial', 'Your Paid Trial Booking is Confirmed!', '<div style=\"font-family: Arial, sans-serif; background-color: #f8f9fa; padding: 30px; color: #333;\">\n      <table style=\"max-width: 600px; margin: 0 auto; background: #fff; border-radius: 8px; overflow: hidden; box-shadow: 0 4px 10px rgba(0,0,0,0.05);\">\n        <tr>\n          <td style=\"background: #2c3e50; padding: 20px; text-align: center; color: #fff;\">\n            <h1 style=\"margin: 0; font-size: 22px; letter-spacing: 0.5px;\">🎉 Booking Paid Trial</h1>\n          </td>\n        </tr>\n        <tr>\n          <td style=\"padding: 25px;\">\n            <h2 style=\"color:#2c3e50; font-size: 20px; margin-bottom: 10px;\">Hi {{parentName}},</h2>\n            <p style=\"font-size: 15px; line-height: 1.6;\">\n              We’re excited to let you know that your <strong>paid trial booking</strong> has been confirmed successfully!\n            </p>\n            <div style=\"background: #f1f4f8; padding: 15px; border-left: 4px solid #3498db; margin: 20px 0;\">\n              <p style=\"margin: 0; font-size: 15px; line-height: 1.6;\">\n                <strong>📍 Venue:</strong> {{venueName}} <br>\n                <strong>📅 Date:</strong> {{trialDate}} <br>\n                <strong>⏰ Time:</strong> {{classTime}} <br>\n                <strong>🎓 Class:</strong> {{className}} <br>\n                             </p>\n            </div>\n            <div style=\"background: #eaf6ff; padding: 15px; border-left: 4px solid #2980b9; margin: 20px 0;\">\n              <p style=\"margin: 0; font-size: 15px; line-height: 1.6;\">\n                <strong>👤 Your Login Credentials:</strong><br>\n                <strong>Email:</strong> {{parentEmail}}<br>\n                <strong>Password:</strong> {{parentPassword}}\n              </p>\n            </div>\n            <p style=\"font-size: 15px; line-height: 1.6;\">\n              ✅ Please arrive <strong>10 minutes early</strong>.<br>\n              ✅ Bring a water bottle & comfortable sports shoes.<br>\n              ✅ Our team will guide you upon arrival.\n            </p>\n            <p style=\"font-size: 15px; line-height: 1.6; margin-top: 20px;\">\n              We’re looking forward to welcoming you!\n            </p>\n            <p style=\"margin-top: 25px; font-size: 15px;\">\n              Best regards, <br>\n              <strong>{{appName}} Team</strong>\n            </p>\n          </td>\n        </tr>\n        <tr>\n          <td style=\"background: #f1f4f8; padding: 15px; text-align: center; font-size: 13px; color: #666;\">\n            &copy; {{year}} {{appName}}. All rights reserved.\n          </td>\n        </tr>\n      </table>\n    </div><div style=\"max-width:600px; background-color:#fff; margin:0 auto; font-family: \'Google Sans\', Arial, sans-serif;\">\n\n    <!-- Banner Section -->\n    <section style=\"text-align:center; padding: 20px 0;\">\n        <img src=\"{{logoUrl}}\" alt=\"Company Logo\" style=\"width:150px; max-width:80%; height:auto;\" />\n    </section>\n\n    <!-- Intro Section -->\n    <section style=\"padding:20px 0 0 0;\">\n        <div style=\"max-width:465px; padding:0 15px; margin:0 auto;\">\n\n            <div style=\"text-align:center; padding-bottom:0; margin:0 auto;\">\n                <p style=\"font-size:14px; line-height:23px; color:#5F5F6D; font-weight:400; margin:0; padding-top:12px;\">\n                    Thank you for choosing to book your child\'s membership with Samba Soccer Schools!<br>\n                    We\'re excited to have them join our classes and be part of our community.\n                </p>\n            </div>\n\n            <div style=\"text-align:center; margin:20px 0; padding:10px; background:#f5f5f5; border-radius:8px;\">\n                <p style=\"font-size:14px; color:#34353B; margin:5px 0;\">\n                    Your membership details:\n                </p>\n                <p style=\"font-size:14px; color:#5F5F6D; margin:5px 0;\">\n                    Email: <strong>{{parentEmail}}</strong><br>\n                </p>\n            </div>\n\n            <!-- Make a Note Heading -->\n            <div>\n                <h2 style=\"text-align:center; margin-top:0; font-family:\'Permanent Marker\'; color:#00a6e3; font-size:40px; padding:24px 0 0; text-transform:capitalize;\">\n                    <img src=\"/find-img/Make-a-Note.png\" alt=\"\" />\n                </h2>\n            </div>\n\n            <!-- Student Info -->\n            <div style=\"border-top:4px solid #0DD180; border-radius:10px; padding:10px; margin:0 0 20px; background-color:#f5f5f5;\">\n                <table style=\"width:100%; border-collapse:collapse;\">\n                    <tbody>\n                        <tr>\n                            <td style=\"width:30%; padding:5px; vertical-align:top;\">\n                                <p style=\"margin:0; font-size:13px; color:#34353B; font-weight:600;\">Name of Student(s):</p>\n                                <p style=\"margin:0; font-size:13px; color:#5F5F6D;\">{{studentFirstName}} {{studentLastName}}</p>\n                            </td>\n                            <td style=\"width:20%; padding:5px; vertical-align:top;\">\n                                <p style=\"margin:0; font-size:13px; color:#34353B; font-weight:600;\">Age Group:</p>\n                                <p style=\"margin:0; font-size:13px; color:#5F5F6D;\">{{className}}</p>\n                            </td>\n                            <td style=\"width:25%; padding:5px; vertical-align:top;\">\n                                <p style=\"margin:0; font-size:13px; color:#34353B; font-weight:600;\">Class Time:</p>\n                                <p style=\"margin:0; font-size:13px; color:#5F5F6D;\">{{classTime}}</p>\n                            </td>\n                            <td style=\"width:25%; padding:5px; vertical-align:top;\">\n                                <p style=\"margin:0; font-size:13px; color:#34353B; font-weight:600;\">Next Payment Date:</p>\n                                <p style=\"margin:0; font-size:13px; color:#5F5F6D;\">{{nextPaymentDate}}</p>\n                            </td>\n                        </tr>\n                    </tbody>\n                </table>\n            </div>\n\n            <!-- Venue Section -->\n            <div style=\"border-top:4px solid #0DD180; border-radius:10px; padding:20px; margin:20px 0; background-color:#5f5f6d17;\">\n                <h3 style=\"margin:0; font-weight:600; font-size:15px; color:#34353B; padding-bottom:5px;\">Venue</h3>\n                <p style=\"margin:0; font-size:13px; color:#5F5F6D;\">\n                    <a href=\"#\" style=\"text-decoration:underline; color:#34353B;\">{{venueName}}</a>\n                    <a href=\"https://www.google.com/maps/search/?api=1&query={{venueName}}\" style=\"font-size:13px; text-decoration:underline; color:#5F5F6D; padding-left:5px;\">See location</a>\n                </p>\n            </div>\n\n            <!-- Image Section -->\n            <div style=\"text-align:center; margin:50px 0 14px;\">\n                <img src=\"{{kidsPlaying}}\" alt=\"Kids playing soccer\" style=\"width:100%;\" />\n            </div>\n\n            <!-- Membership Benefits Button -->\n            <div style=\"text-align:center; margin-bottom:22px; margin-top:40px;\">\n                <button style=\"font-family: \'Google Sans\', Arial, sans-serif; background-color:#FFDE14; padding:8px 16px; border:none; border-radius:14px; color:#042C89; font-size:18px; font-weight:900;\">Membership Benefits</button>\n            </div>\n\n            <!-- Info Sections -->\n            <div>\n                <b style=\"color:#042C89; font-weight:600; font-size:17px;\">Arrival Time</b>\n                <p style=\"color:#3E3E47; font-size:13px; padding:5px 0 30px 0; margin:0;\">Please arrive at least 10 minutes before each session.</p>\n\n                <b style=\"color:#042C89; font-weight:600; font-size:17px;\">What to Bring</b>\n                <p style=\"color:#3E3E47; font-size:13px; padding:5px 0 30px 0; margin:0;\">Ensure your child has their membership kit, proper trainers, and water for the sessions.</p>\n\n                <b style=\"color:#042C89; font-weight:600; font-size:17px;\">Exclusive Membership Benefits</b>\n                <p style=\"color:#3E3E47; font-size:13px; padding:5px 0 30px 0; margin:0;\">\n                    Members enjoy full access to all classes, special training sessions, and a free uniform.<br>\n                    Book additional sessions or access member-only content via: <a href=\"https://webstepdev.com/demo/synco/\" style=\"color:#3E3E47;\">https://webstepdev.com/demo/synco/</a>\n                </p>\n\n                <b style=\"color:#042C89; font-weight:600; font-size:17px;\">Cancellations & Changes</b>\n                <p style=\"color:#3E3E47; font-size:13px; padding:5px 0 30px 0; margin:0;\">Membership bookings can be rescheduled with prior notice. We will contact you in case of any changes due to unforeseen circumstances.</p>\n\n                <b style=\"color:#042C89; font-weight:600; font-size:17px;\">Stay Connected</b>\n                <p style=\"color:#3E3E47; font-size:13px; padding:5px 0 30px 0; margin:0;\">\n                    Follow our latest updates, photos, and events via Facebook and Instagram.<br>\n                    Visit our website: <a href=\"https://webstepdev.com/demo/synco/\" style=\"color:#3E3E47;\">https://webstepdev.com/demo/synco/</a>\n                </p>\n            </div>\n\n            <p style=\"color:#00a6e3; font-size:17px; text-align:center; margin:40px 0; font-weight:500;\">\n                We look forward to seeing your child in our classes. For questions, please call us on 020 7205 2723.\n            </p>\n\n            <!-- Banner Image -->\n            <section style=\"text-align:center; padding: 20px 0;\">\n                <img src=\"{{logoUrl}}\" alt=\"Company Logo\" style=\"width:150px; max-width:80%; height:auto;\" />\n            </section>\n\n            <p style=\"color:#797A88; font-size:13px; padding:4px 42px; text-align:center; line-height:23px;\">\n                Enhance your child\'s skills at home! Practice the ball mastery moves below to stay sharp.\n            </p>\n\n            <!-- Skills Section -->\n            <div style=\"display:flex; flex-wrap:wrap; gap:10px; margin-top:30px;\">\n                <!-- Skill 1 -->\n                <div style=\"padding:10px; border:1px solid #eee; border-radius:10px; flex:1 1 48%; box-sizing:border-box; background-color:#fff;\">\n                    <a href=\"https://www.youtube.com/watch?v=JEWKJbo6jSg&feature=youtu.be\" style=\"text-decoration:none;\">\n                        <img src=\"/img-form/rounded3.png\" alt=\"The Rolar\" style=\"width:100%; border-radius:10px;\" />\n                        <div style=\"padding:5px;\">\n                            <p style=\"font-size:15px; font-weight:700; color:#042c89; margin:10px 0;\">The Rolar</p>\n                            <p style=\"font-size:12px; color:#5f5f6d; font-weight:400;\">Learn the \"Rolar\" skill to move past defenders effectively.</p>\n                        </div>\n                    </a>\n                </div>\n\n                <!-- Skill 2 -->\n                <div style=\"padding:10px; border:1px solid #eee; border-radius:10px; flex:1 1 48%; box-sizing:border-box; background-color:#fff;\">\n                    <a href=\"https://www.youtube.com/watch?v=C49Bos5QAFw\" style=\"text-decoration:none;\">\n                        <img src=\"/img-form/rounded2.png\" alt=\"The Fatia\" style=\"width:100%; border-radius:10px;\" />\n                        <div style=\"padding:5px;\">\n                            <p style=\"font-size:15px; font-weight:700; color:#042c89; margin:10px 0;\">The Fatia</p>\n                            <p style=\"font-size:12px; color:#5f5f6d; font-weight:400;\">Perfect your ball control and dribbling with \"Fatia\".</p>\n                        </div>\n                    </a>\n                </div>\n\n                <!-- Skill 3 -->\n                <div style=\"padding:10px; border:1px solid #eee; border-radius:10px; flex:1 1 48%; box-sizing:border-box; background-color:#fff;\">\n                    <a href=\"https://www.youtube.com/watch?v=g24r83kQr_0&ab_channel=SambaSoccerSchools%C2%AE\" style=\"text-decoration:none;\">\n                        <img src=\"/img-form/rounded1.png\" alt=\"The Inside Pedalada Turn\" style=\"width:100%; border-radius:10px;\" />\n                        <div style=\"padding:5px;\">\n                            <p style=\"font-size:15px; font-weight:700; color:#042c89; margin:10px 0;\">Inside Pedalada Turn</p>\n                            <p style=\"font-size:12px; color:#5f5f6d; font-weight:400;\">Master this move to easily beat defenders on the field.</p>\n                        </div>\n                    </a>\n                </div>\n\n                <!-- Skill 4 -->\n                <div style=\"padding:10px; border:1px solid #eee; border-radius:10px; flex:1 1 48%; box-sizing:border-box; background-color:#fff;\">\n                    <a href=\"https://www.youtube.com/watch?v=gP5aUR5BGpU&ab_channel=SambaSoccerSchools%C2%AE\" style=\"text-decoration:none;\">\n                        <img src=\"/img-form/rounded.png\" alt=\"The Roll Back\" style=\"width:100%; border-radius:10px;\" />\n                        <div style=\"padding:5px;\">\n                            <p style=\"font-size:15px; font-weight:700; color:#042c89; margin:10px 0;\">The Roll Back</p>\n                            <p style=\"font-size:12px; color:#5f5f6d; font-weight:400;\">Learn the \"Roll Back\" to improve your child’s ball control.</p>\n                        </div>\n                    </a>\n                </div>\n\n            </div>\n\n        </div>\n    </section>\n\n</div>\n', 'smtp.gmail.com', 1, 465, 'kapilakshu848@gmail.com', 'zlgninioettnhazf', 'kapilakshu848@gmail.com', 'Synco', 1, '[ \"{{parentName}}\", \"{{studentName}}\", \"{{venueName}}\", \"{{className}}\", \"{{classTime}}\", \"{{trialDate}}\", \"{{parentEmail}}\", \"{{parentPassword}}\", \"{{appName}}\", \"{{year}}\" ][ \"{{parentName}}\", \"{{studentName}}\", \"{{venueName}}\", \"{{className}}\", \"{{classTime}}\", \"{{trialDate}}\", \"{{parentEmail}}\", \"{{parentPassword}}\", \"{{appName}}\", \"{{year}}\",\"{{logoUrl}\",\"{{kidsPlaying}} ]', NULL, NULL, NULL, '2025-08-18 17:36:01', '2025-08-18 17:36:01'),
(12, 'admin', 'booking-status', 'Booking Status Update', '<div style=\"font-family: Arial, sans-serif; color:#333;\">\n       <p>Hello {{parentName}},</p>\n       <p>This is to inform you that the booking for your child <strong>{{studentName}}</strong> has the following status:</p>\n       <p><strong>Status: {{status}}</strong></p>\n       <p>Thank you,<br>Synco Team</p>\n     </div>', 'smtp.gmail.com', 1, 465, 'kapilakshu848@gmail.com', 'zlgninioettnhazf', 'kapilakshu848@gmail.com', 'Synco', 1, '[ \"{{parentName}}\", \"{{studentName}}\", \"{{status}}\" ]', NULL, NULL, NULL, '2025-08-18 17:04:41', '2025-08-18 17:04:41'),
(13, 'admin', 'booking-active-status', 'Booking Status Update', '<div style=\"font-family: Arial, sans-serif; color:#333;\">\r\n        <p>Hello {{parentName}},</p>\r\n        <p>This is to inform you that the booking for your child <strong>{{studentName}}</strong> has the following status:</p>\r\n        <p><strong>Status: {{status}}</strong></p>\r\n        <p>Thank you,<br>Synco Team</p>\r\n     </div>', 'smtp.gmail.com', 1, 465, 'kapilakshu848@gmail.com', 'zlgninioettnhazf', 'kapilakshu848@gmail.com', 'Synco', 1, '[ \"{{parentName}}\", \"{{studentName}}\", \"{{status}}\" ]', NULL, NULL, NULL, '2025-08-19 15:23:44', '2025-08-19 15:23:44');
INSERT INTO `email_configs` (`id`, `module`, `action`, `subject`, `html_template`, `smtp_host`, `smtp_secure`, `smtp_port`, `smtp_username`, `smtp_password`, `from_email`, `from_name`, `status`, `variables`, `to`, `cc`, `bcc`, `createdAt`, `updatedAt`) VALUES
(14, 'admin', 'waiting-list', 'Your Booking is on Waiting List', '<div style=\"max-width:600px; background-color:#fff; margin:0 auto; font-family: \'Google Sans\', Arial, sans-serif;\">\n\n    <!-- Banner Section -->\n    <section style=\"text-align:center; padding: 20px 0;\">\n        <img src=\"{{logoUrl}}\" alt=\"Company Logo\" style=\"width:150px; max-width:80%; height:auto;\" />\n    </section>\n\n    <!-- Intro Section -->\n    <section style=\"padding:20px 0 0 0;\">\n        <div style=\"max-width:465px; padding:0 15px; margin:0 auto;\">\n\n            <div style=\"text-align:center; padding-bottom:0; margin:0 auto;\">\n                <p style=\"font-size:14px; line-height:23px; color:#5F5F6D; font-weight:400; margin:0; padding-top:12px;\">\n                    Thank you for your interest in Samba Soccer Schools!<br>\n                    Your child has been successfully added to our waiting list for the upcoming classes. We will notify you as soon as a spot becomes available.\n                </p>\n            </div>\n\n            <div style=\"text-align:center; margin:20px 0; padding:10px; background:#f5f5f5; border-radius:8px;\">\n                <p style=\"font-size:14px; color:#34353B; margin:5px 0;\">\n                    Your waiting list details:\n                </p>\n                <p style=\"font-size:14px; color:#5F5F6D; margin:5px 0;\">\n                    Email: <strong>{{parentEmail}}</strong><br>\n                    Password: <strong>{{parentPassword}}</strong><br>\n                </p>\n            </div>\n\n            <!-- Make a Note Heading -->\n            <div>\n                <h2 style=\"text-align:center; margin-top:0; font-family:\'Permanent Marker\'; color:#00a6e3; font-size:40px; padding:24px 0 0; text-transform:capitalize;\">\n                    <img src=\"/find-img/Make-a-Note.png\" alt=\"\" />\n                </h2>\n            </div>\n\n            <!-- Student Info -->\n            <div style=\"border-top:4px solid #0DD180; border-radius:10px; padding:10px; margin:0 0 20px; background-color:#f5f5f5;\">\n                <table style=\"width:100%; border-collapse:collapse;\">\n                    <tbody>\n                        <tr>\n                            <td style=\"width:30%; padding:5px; vertical-align:top;\">\n                                <p style=\"margin:0; font-size:13px; color:#34353B; font-weight:600;\">Name of Student(s):</p>\n                                <p style=\"margin:0; font-size:13px; color:#5F5F6D;\">{{studentFirstName}} {{studentLastName}}</p>\n                            </td>\n                            <td style=\"width:20%; padding:5px; vertical-align:top;\">\n                                <p style=\"margin:0; font-size:13px; color:#34353B; font-weight:600;\">Age Group:</p>\n                                <p style=\"margin:0; font-size:13px; color:#5F5F6D;\">{{className}}</p>\n                            </td>\n                            <td style=\"width:25%; padding:5px; vertical-align:top;\">\n                                <p style=\"margin:0; font-size:13px; color:#34353B; font-weight:600;\">Requested Time:</p>\n                                <p style=\"margin:0; font-size:13px; color:#5F5F6D;\">{{classTime}}</p>\n                            </td>\n                            <td style=\"width:25%; padding:5px; vertical-align:top;\">\n\n                            </td>\n                        </tr>\n                    </tbody>\n                </table>\n            </div>\n\n            <!-- Venue Section -->\n            <div style=\"border-top:4px solid #0DD180; border-radius:10px; padding:20px; margin:20px 0; background-color:#5f5f6d17;\">\n                <h3 style=\"margin:0; font-weight:600; font-size:15px; color:#34353B; padding-bottom:5px;\">Venue</h3>\n                <p style=\"margin:0; font-size:13px; color:#5F5F6D;\">\n                    <a href=\"#\" style=\"text-decoration:underline; color:#34353B;\">{{venueName}}</a>\n                    <a href=\"https://www.google.com/maps/search/?api=1&query={{venueName}}\" style=\"font-size:13px; text-decoration:underline; color:#5F5F6D; padding-left:5px;\">See location</a>\n                </p>\n            </div>\n\n            <!-- Image Section -->\n            <div style=\"text-align:center; margin:50px 0 14px;\">\n                <img src=\"{{kidsPlaying}}\" alt=\"Kids playing soccer\" style=\"width:100%;\" />\n            </div>\n\n            <!-- Waiting List Info Button -->\n            <div style=\"text-align:center; margin-bottom:22px; margin-top:40px;\">\n                <button style=\"font-family: \'Google Sans\', Arial, sans-serif; background-color:#FFDE14; padding:8px 16px; border:none; border-radius:14px; color:#042C89; font-size:18px; font-weight:900;\">Waiting List Info</button>\n            </div>\n\n            <!-- Info Sections -->\n            <div>\n                <b style=\"color:#042C89; font-weight:600; font-size:17px;\">Next Steps</b>\n                <p style=\"color:#3E3E47; font-size:13px; padding:5px 0 30px 0; margin:0;\">We will notify you via email or SMS as soon as a spot opens up in the requested class.</p>\n\n                <b style=\"color:#042C89; font-weight:600; font-size:17px;\">What to Prepare</b>\n                <p style=\"color:#3E3E47; font-size:13px; padding:5px 0 30px 0; margin:0;\">Ensure your child is ready with appropriate clothing and trainers so they can join the class immediately once a spot is available.</p>\n\n                <b style=\"color:#042C89; font-weight:600; font-size:17px;\">Waiting List Benefits</b>\n                <p style=\"color:#3E3E47; font-size:13px; padding:5px 0 30px 0; margin:0;\">\n                    While on the waiting list, your child can access online resources and video tutorials to keep practicing.<br>\n                    Visit: <a href=\"https://webstepdev.com/demo/synco/\" style=\"color:#3E3E47;\">https://webstepdev.com/demo/synco/</a>\n                </p>\n\n                <b style=\"color:#042C89; font-weight:600; font-size:17px;\">Stay Connected</b>\n                <p style=\"color:#3E3E47; font-size:13px; padding:5px 0 30px 0; margin:0;\">\n                    Follow our latest updates, photos, and announcements via Facebook and Instagram.<br>\n                    Visit our website: <a href=\"https://webstepdev.com/demo/synco/\" style=\"color:#3E3E47;\">https://webstepdev.com/demo/synco/</a>\n                </p>\n            </div>\n\n            <p style=\"color:#00a6e3; font-size:17px; text-align:center; margin:40px 0; font-weight:500;\">\n                We look forward to welcoming your child as soon as a spot becomes available. For questions, please call us on 020 7205 2723.\n            </p>\n\n            <!-- Banner Image -->\n            <section style=\"text-align:center; padding: 20px 0;\">\n                <img src=\"{{logoUrl}}\" alt=\"Company Logo\" style=\"width:150px; max-width:80%; height:auto;\" />\n            </section>\n\n            <p style=\"color:#797A88; font-size:13px; padding:4px 42px; text-align:center; line-height:23px;\">\n                While waiting, you can help your child practice soccer skills at home or in a nearby park!\n            </p>\n\n            <!-- Skills Section -->\n            <div style=\"display:flex; flex-wrap:wrap; gap:10px; margin-top:30px;\">\n                <!-- Skill 1 -->\n                <div style=\"padding:10px; border:1px solid #eee; border-radius:10px; flex:1 1 48%; box-sizing:border-box; background-color:#fff;\">\n                    <a href=\"https://www.youtube.com/watch?v=JEWKJbo6jSg&feature=youtu.be\" style=\"text-decoration:none;\">\n                        <img src=\"/img-form/rounded3.png\" alt=\"The Rolar\" style=\"width:100%; border-radius:10px;\" />\n                        <div style=\"padding:5px;\">\n                            <p style=\"font-size:15px; font-weight:700; color:#042c89; margin:10px 0;\">The Rolar</p>\n                            <p style=\"font-size:12px; color:#5f5f6d; font-weight:400;\">Learn the \"Rolar\" skill to move past defenders effectively.</p>\n                        </div>\n                    </a>\n                </div>\n\n                <!-- Skill 2 -->\n                <div style=\"padding:10px; border:1px solid #eee; border-radius:10px; flex:1 1 48%; box-sizing:border-box; background-color:#fff;\">\n                    <a href=\"https://www.youtube.com/watch?v=C49Bos5QAFw\" style=\"text-decoration:none;\">\n                        <img src=\"/img-form/rounded2.png\" alt=\"The Fatia\" style=\"width:100%; border-radius:10px;\" />\n                        <div style=\"padding:5px;\">\n                            <p style=\"font-size:15px; font-weight:700; color:#042c89; margin:10px 0;\">The Fatia</p>\n                            <p style=\"font-size:12px; color:#5f5f6d; font-weight:400;\">Perfect your ball control and dribbling with \"Fatia\".</p>\n                        </div>\n                    </a>\n                </div>\n\n                <!-- Skill 3 -->\n                <div style=\"padding:10px; border:1px solid #eee; border-radius:10px; flex:1 1 48%; box-sizing:border-box; background-color:#fff;\">\n                    <a href=\"https://www.youtube.com/watch?v=g24r83kQr_0&ab_channel=SambaSoccerSchools%C2%AE\" style=\"text-decoration:none;\">\n                        <img src=\"/img-form/rounded1.png\" alt=\"The Inside Pedalada Turn\" style=\"width:100%; border-radius:10px;\" />\n                        <div style=\"padding:5px;\">\n                            <p style=\"font-size:15px; font-weight:700; color:#042c89; margin:10px 0;\">Inside Pedalada Turn</p>\n                            <p style=\"font-size:12px; color:#5f5f6d; font-weight:400;\">Master this move to easily beat defenders on the field.</p>\n                        </div>\n                    </a>\n                </div>\n\n                <!-- Skill 4 -->\n                <div style=\"padding:10px; border:1px solid #eee; border-radius:10px; flex:1 1 48%; box-sizing:border-box; background-color:#fff;\">\n                    <a href=\"https://www.youtube.com/watch?v=gP5aUR5BGpU&ab_channel=SambaSoccerSchools%C2%AE\" style=\"text-decoration:none;\">\n                        <img src=\"/img-form/rounded.png\" alt=\"The Roll Back\" style=\"width:100%; border-radius:10px;\" />\n                        <div style=\"padding:5px;\">\n                            <p style=\"font-size:15px; font-weight:700; color:#042c89; margin:10px 0;\">The Roll Back</p>\n                            <p style=\"font-size:12px; color:#5f5f6d; font-weight:400;\">Learn the \"Roll Back\" to improve your child’s ball control.</p>\n                        </div>\n                    </a>\n                </div>\n\n            </div>\n\n        </div>\n    </section>\n\n</div>', 'smtp.gmail.com', 1, 465, 'kapilakshu848@gmail.com', 'zlgninioettnhazf', 'kapilakshu848@gmail.com', 'Synco', 1, '[ \"{{parentName}}\", \"{{venueName}}\", \"{{startDate}}\", \"{{classTime}}\", \"{{className}}\", \"{{studentFirstName}}\", \"{{studentLastName}}\", \"{{parentEmail}}\", \"{{parentPassword}}\", \"{{appName}}\", \"{{year}}\" ][ \"{{parentName}}\", \"{{venueName}}\", \"{{startDate}}\", \"{{classTime}}\", \"{{className}}\", \"{{studentFirstName}}\", \"{{studentLastName}}\", \"{{parentEmail}}\", \"{{parentPassword}}\", \"{{appName}}\", \"{{year}}\",{{logoUrl}\",\"{{kidsPlaying}} ]', NULL, NULL, NULL, '2025-09-01 16:20:11', '2025-09-01 16:20:11'),
(15, 'admin', 'cancel-booking', 'Notice: Your Booking Has Been Cancelled', '<!DOCTYPE html>\r\n  <html>\r\n  <head>\r\n    <meta charset=\"UTF-8\" />\r\n    <title>Booking Cancellation</title>\r\n  </head>\r\n  <body style=\"font-family: Arial, sans-serif; background-color:#f9f9f9; margin:0; padding:0;\">\r\n    <table width=\"100%\" cellpadding=\"0\" cellspacing=\"0\" style=\"background-color:#f9f9f9; padding:20px;\">\r\n      <tr>\r\n        <td align=\"center\">\r\n          <table width=\"600\" cellpadding=\"0\" cellspacing=\"0\" style=\"background:#ffffff; border-radius:8px; overflow:hidden; box-shadow:0 2px 6px rgba(0,0,0,0.1);\">\r\n            <tr>\r\n              <td style=\"background:#2b6cb0; color:#ffffff; padding:20px; text-align:center; font-size:20px; font-weight:bold;\">\r\n                Synco | Booking Update\r\n              </td>\r\n            </tr>\r\n            <tr>\r\n              <td style=\"padding:20px; color:#333333; font-size:15px; line-height:1.6;\">\r\n                <p>Dear {{parentName}},</p>\r\n                <p>We regret to inform you that the {{bookingType}} class for <strong>{{studentName}}</strong> has been cancelled.</p>\r\n                <p>\r\n                  <strong>Class:</strong> {{className}}<br/>\r\n                  <strong>Venue:</strong> {{venueName}}<br/>\r\n                  <strong>Date:</strong> {{startDate}}<br/>\r\n                  <strong>Time:</strong> {{startTime}} - {{endTime}}<br/>\r\n                  <strong>Reason:</strong> {{cancelReason}}\r\n                </p>\r\n                {{additionalNote}}\r\n                <p>We sincerely apologize for the inconvenience and will be happy to assist in rescheduling another class at your convenience.</p>\r\n                <p style=\"margin-top:20px;\">Best Regards,<br/>The Synco Team</p>\r\n              </td>\r\n            </tr>\r\n            <tr>\r\n              <td style=\"background:#f1f1f1; padding:15px; text-align:center; font-size:13px; color:#666666;\">\r\n                &copy; {{year}} Synco. All rights reserved.\r\n              </td>\r\n            </tr>\r\n          </table>\r\n        </td>\r\n      </tr>\r\n    </table>\r\n  </body>\r\n  </html>', 'smtp.gmail.com', 1, 465, 'kapilakshu848@gmail.com', 'zlgninioettnhazf', 'kapilakshu848@gmail.com', 'Synco', 1, '[ \"{{parentName}}\", \"{{venueName}}\", \"{{startDate}}\", \"{{classTime}}\", \"{{className}}\", \"{{studentFirstName}}\", \"{{studentLastName}}\", \"{{parentEmail}}\", \"{{parentPassword}}\", \"{{appName}}\", \"{{year}}\" ]', NULL, NULL, NULL, '2025-09-05 10:56:13', '2025-09-05 10:56:13');
